mapper.select:
SELECT cr.*, u.work_id, u.name, u.avatar, u.department, u.mobile, u.status, u.last_active_time FROM dbo.contacts_relation cr JOIN dbo.[user] u ON cr.contact_id = u.id WHERE cr.user_id = #{userId}
AND (u.name LIKE CONCAT('%', #{keyword}, '%') OR u.work_id LIKE CONCAT('%', #{keyword}, '%') OR u.department LIKE CONCAT('%', #{keyword}, '%'))
AND u.department = #{department}
AND u.status = #{status}
ORDER BY u.last_active_time DESC

SELECT COUNT(*) FROM dbo.contacts_relation cr JOIN dbo.[user] u ON cr.contact_id = u.id WHERE cr.user_id = #{userId}
AND (u.name LIKE CONCAT('%', #{keyword}, '%') OR u.work_id LIKE CONCAT('%', #{keyword}, '%') OR u.department LIKE CONCAT('%', #{keyword}, '%'))
AND u.department = #{department}
AND u.status = #{status}

mapper.insert:
INSERT INTO dbo.contacts_relation (
    user_id, contact_id, relation_type, remark, create_time, update_time
) VALUES (
    #{userId}, #{contactId}, #{relationType}, #{remark}, #{createTime}, #{updateTime}
)

mapper.delete:
DELETE FROM dbo.contacts_relation
WHERE
    user_id = #{userId} AND contact_id = #{contactId}
